def makeZero(n):
  #lanjutkan kode tanpa menggunakan library numpy
  inisiasi = range(n)
  inisiasi = []
  i = 0
  for i in range(n) :
    inisiasi.append(0)
    #print(inisiasi)
  return inisiasi
  
print("inisiasi zero list menggunakan fungsi python",makeZero(10))
#dengan numpy
