#buat fungsi
def shape(matriks):
  #lanjutkan kode tanpa menggunakan library numpy
  a = matriks
  for i in range(len(matriks)) :
    for j in range(len(matriks[i])) :
      b = matriks[i]
  c = (len(a),len(b))
  return c

