matriks1=np.array([49,27,10,30,22,71,88,39,66,22,14,90,21,20,18,7,9,10])
#without numpy
idx = 2
res1 = sorted(range(len(matriks1)), key = lambda sub: matriks1[sub])[:idx]
res = sorted(range(len(matriks1)), key = lambda sub: matriks1[sub])[-idx:]
