#with numpy
matriks=np.array([49,27,10,30,22,71,88,39,66,22,14,90,21,20,18,7,9,10])
#ubahlah 2 nilai terkecil array menjadi 0
a,b = np.partition(matriks,1)[0:2]
matriks=np.where(matriks > b, matriks,0)

#kemudian ubahlah 2 nilai terbesar menjadi 1
a,b=(matriks[np.argpartition(matriks,-2)[-2:]])
matriks=np.where(matriks < b-2, matriks,1)

#output matriks tersebut
print("hasil menggunakan library numpy: ")
print(matriks)

